import"./hoisted.CNm4v15S.js";document.getElementById("playerForm")?.addEventListener("submit",async t=>{t.preventDefault();const e=new FormData(t.target),o={name:e.get("name"),number:parseInt(e.get("number")),position:e.get("position"),photo:"/player.webp"};try{const r=await fetch("/api/players",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(o)});if(!r.ok){const a=await r.json();throw new Error(a.message||"Error creating player")}window.location.href="/admin/players"}catch(r){console.error("Error:",r),alert(r instanceof Error?r.message:"Error creating player")}});
