import"./hoisted.CNm4v15S.js";document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".delete-article").forEach(r=>{r.addEventListener("click",async t=>{const o=t.target.dataset.articleId;if(confirm("Êtes-vous sûr de vouloir supprimer cette actualité ?"))try{const e=await fetch(`/api/news/${o}`,{method:"DELETE"});if(!e.ok){const a=await e.json();throw new Error(a.message||"Error deleting article")}window.location.reload()}catch(e){console.error("Error:",e),alert(e instanceof Error?e.message:"Error deleting article")}})})});
