import"./hoisted.CNm4v15S.js";document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".delete-player").forEach(r=>{r.addEventListener("click",async o=>{const t=o.target.dataset.playerId;if(confirm("Êtes-vous sûr de vouloir supprimer ce joueur ?"))try{const e=await fetch(`/api/players/${t}`,{method:"DELETE"});if(!e.ok){const a=await e.json();throw new Error(a.message||"Error deleting player")}window.location.reload()}catch(e){console.error("Error:",e),alert(e instanceof Error?e.message:"Error deleting player")}})})});
