import"./hoisted.CNm4v15S.js";document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".delete-match").forEach(r=>{r.addEventListener("click",async t=>{const o=t.target.dataset.matchId;if(confirm("Êtes-vous sûr de vouloir supprimer ce match ?"))try{const e=await fetch(`/api/matches/${o}`,{method:"DELETE"});if(!e.ok){const a=await e.json();throw new Error(a.message||"Error deleting match")}window.location.reload()}catch(e){console.error("Error:",e),alert(e instanceof Error?e.message:"Error deleting match")}})})});
